      <TextBlock Text="{Binding Path=Data.Name}" HorizontalAlignment="Left" />
      <TextBlock Text="{Binding Path=Data.Address}" HorizontalAlignment="Left" />
    </StackPanel>
  </Border>
</DataTemplate>

